import './App.scss';
import React, {Component} from 'react';
import Select from 'react-select';
import Room from './Room';


const { connect } = require('twilio-video');

const options = [
  { value: '', label: '⠀' },
  { value: 'spanish', label: 'Spanish' },
  { value: 'english', label: 'English' },
  { value: 'german', label: 'German' },
];

class App extends Component {
    constructor(props) {
        super(props)

        this.state = {
            identity: '',
            selectedOption: null,
            room: null
        }
        this.inputRef = React.createRef();
        this.joinRoom = this.joinRoom.bind(this);
        this.returnToLobby = this.returnToLobby.bind(this);
        this.updateIdentity = this.updateIdentity.bind(this);
        this.removePlaceholderText = this.removePlaceholderText.bind(this)
    }




    handleChange = selectedOption => {
        this.setState({ selectedOption });
        console.log(`Option selected:`, selectedOption);
        console.log(`Popo:`,this.state.selectedOption);

    };

    async joinRoom() {
        try {
            const response = await fetch(`https://warm-hollows-35856.herokuapp.com/getTwilioToken?identity=${this.state.identity}`);

            const data = await response.json();
            console.log(data);
            
            const room = await connect(data.token, {
                name: this.state.selectedOption,
                audio: true,
                video: false
            });

            this.setState({ room: room });
        } catch(err) {
            console.log(err);
        }
    }


    returnToLobby() {
        this.setState({ room: null });
    }


    removePlaceholderText() {
        this.inputRef.current.placeholder = '';

    }


    updateIdentity(event) {
        this.setState({
            identity: event.target.value
        });
    }



    render() {
        const disabled = this.state.identity === '' ? true : false;
        
        
        
        return (
            <div className="app">
                {
                    this.state.room === null
                        ? <div className="lobby">
                            <input
                                value={this.state.identity}
                                onChange={this.updateIdentity}
                                ref={this.inputRef}
                                onClick={this.removePlaceholderText}
                                placeholder="What's your name?"/>
                            <button disabled={disabled} onClick={this.joinRoom}>Join Room</button>

                             <Select
                                value={this.state.selectedOption}
                                onChange={this.handleChange}
                                options={options}
                              />
                              
                            
                        </div>
                        : <Room returnToLobby={this.returnToLobby} room={this.state.room} />
                }
            </div>
        );
    }
}

export default App;
